import React, {useState, FormEvent} from 'react';
import {useHistory} from 'react-router-dom';
import PageHeader from '../../components/PageHeader';
import './styles.css'
import Input from '../../components/Input';
import warningIcon from '../../assets/images/icons/warning.svg';
import Textarea from '../../components/Textarea';
import Select from '../../components/Select';
import api from '../../services/api';

function TeacherForm() {
    const history = useHistory();


    const [name, setName] = useState('');
    const [avatar, setAvatar] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [bio, setBio] = useState('');

    const [subject, setSubject] = useState('');
    const [cost, setCost] = useState('');

    const [scheduleItems, setScheduleItems] = useState([
        {week_day: 0,from: '', to: ''}, //cada {} tem um parametro do array
    ]);

    function addNewScheduleItem() {
        setScheduleItems([
            ...scheduleItems,
            {week_day: 0, from: '', to: ''}
        ]);
    }

    function setScheduleItemValue(position: number, field: string, value: string){
        const updatedScheduleItems = scheduleItems.map((scheduleItem, index) =>{ //o map percorre o array e retorna o array sempre com a mesma quantidade de campos
            if (index === position){
                return {...scheduleItem, [field]: value};
            }
            return scheduleItem;
        })
        setScheduleItems(updatedScheduleItems);
    }

    function handleCreateClass(e: FormEvent){
        e.preventDefault(); //impedir que aconteça o submit em baixo redirecione quando se clica nele

        api.post('classes' , {
            name,
            avatar,
            whatsapp,
            bio,
            subject,
            cost: Number(cost),
            schedule: scheduleItems
        }).then(() =>{
            alert('Registo realizado com sucesso!');
            history.push('/');

        }).catch(() =>{
            alert('Erro no cadastro!');
        })
    }


    return(
        <div id="page-teacher-form" className="container">
            <PageHeader 
                title="Que incrivel que queres dar aulas!"
                description="Preencha este formulário"
            />
        

            <main>
                <form onSubmit= {handleCreateClass}>                    
                    <fieldset>
                        <legend>Seus dados</legend>
                        
                        <Input name="name" 
                            label="Nome completo" 
                            value = {name} 
                            onChange={(e) => {setName(e.target.value) }}
                        />

                        <Input 
                            name="avatar" 
                            label="Avatar"
                            value = {avatar} 
                            onChange={(e) => {setAvatar(e.target.value) }}
                        />

                        <Input 
                            name="whatsapp" 
                            label="Whatsapp"
                            value = {whatsapp} 
                            onChange={(e) => {setWhatsapp(e.target.value) }}
                        />

                        <Textarea 
                            name="bio" 
                            label="Biografia"
                            value = {bio} 
                            onChange={(e) => {setBio(e.target.value) }}
                        />
            
                    </fieldset>
                    <fieldset>

                        <legend>Sobre a aula</legend>

                        <Select 
                            name="subject"
                            label="Matéria"
                            value = {subject} 
                            onChange={(e) => {setSubject(e.target.value) }}
                            options = {[
                                {value: 'Artes', label: 'Artes'},
                                {value: 'Biologia', label: 'Biologia'},
                                {value: 'Ciências', label: 'Ciências'},
                                {value: 'Educação Fisíca', label: 'Educação Fisíca'},
                                {value: 'Física', label: 'Física'},
                                {value: 'Geografia', label: 'Geografia'},
                                {value: 'História', label: 'História'},
                                {value: 'Matemática', label: 'Matemática'},
                                {value: 'Português', label: 'Português'},
                                {value: 'Química', label: 'Química'},
                            ]}
                        />
                        
                        <Input 
                            name="Cost" 
                            label="Custo da aula por hora"
                            value = {cost} 
                            onChange={(e) => {setCost(e.target.value) }}
                        />

                    </fieldset>

                        <fieldset>
                            <legend>Horários disponíveis
                                <button type="button" onClick = {addNewScheduleItem}>
                                    + Novo Horário
                                </button>
                            </legend>
                                {scheduleItems.map((scheduleItem, index) => { //criação de como fazer o novo horario
                                    return (                                    //index é a posição do item no array original
                                    <div key ={scheduleItem.week_day}  className="schedule-item"> {/* a key é week day para apenas por um horario por dia da semana */}
                                            <Select 
                                                name="week_day"
                                                label="Dia da semana"
                                                value={scheduleItem.week_day}
                                                onChange={e => setScheduleItemValue(index, 'week_day' , e.target.value)}
                                                options = {[                        //como primeiro parametro o index 2ª o nome do campo e 3 o valor do campo
                                                {value: '0', label: 'Domingo'},
                                                {value: '1', label: 'Segunda-Feira'},
                                                {value: '2', label: 'Terça-feira'},
                                                {value: '3', label: 'Quarta-Feira'},
                                                {value: '4', label: 'Quinta-feira'},
                                                {value: '5', label: 'Sexta-Feira'},
                                                {value: '6', label: 'Sábado'},         
                                            ]}
                                            />
                                        <Input 
                                            name="from" 
                                            label="Das" 
                                            type="time"
                                            value = {scheduleItem.from} 
                                            onChange={(e) => setScheduleItemValue(index, 'from', e.target.value) }
                                        />

                                        <Input 
                                            name="to" 
                                            label="Até" 
                                            type="time"
                                            value ={scheduleItem.to} 
                                            onChange={(e) => setScheduleItemValue(index, 'to', e.target.value) }
                                        /> 
                                    </div>
                             ); 
                            })}       
                        </fieldset>
                    <footer>
                        <p>
                            <img src={warningIcon} alt="Aviso importante"/>
                            Importante! <br/>
                            Preencha todos os dados
                        </p>
                        <button type="submit">
                            Gravar registo
                        </button>
                    </footer>
                </form>  
            </main>
        </div> 
    )
}

export default TeacherForm
